import React, { useEffect, useState } from 'react';
import { useTable } from 'react-table';
import styled from 'styled-components';

function Table({ columns, data }) {
    // Use the state and functions returned from useTable to build your UI
    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = useTable({
        columns,
        data,
    })

    // Render the UI for your table
    return (
        <table {...getTableProps()}>
            <thead>
                {headerGroups.map(headerGroup => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map(column => (
                            <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                        ))}
                    </tr>
                ))}
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map((row, i) => {
                    prepareRow(row)
                    return (
                        <tr {...row.getRowProps()}>
                            {row.cells.map(cell => {
                                return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                            })}
                        </tr>
                    )
                })}
            </tbody>
        </table>
    )
}

const Styles = styled.div`
  padding: 1rem;

  table {
    border-spacing: 0;
    border: 1px solid black;

    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }

    th,
    td {
      margin: 0;
      padding: 0.5rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;

      :last-child {
        border-right: 0;
      }
    }
  }
`

    function Products() {
        const columns = React.useMemo(
            () => [
                {
                    Header: 'Product Information',
                    columns: [
                        {
                            Header: 'Product ID',
                            accessor: 'PID',
                        },
                        {
                            Header: 'Product Name',
                            accessor: 'Name',
                        },
                        {
                            Header: 'Quantity on Hand',
                            accessor: 'Quantity',
                        },
                        {
                            Header: 'Max Quantity',
                            accessor: 'MaxQuantity',
                        },
                        {
                            Header: 'Item Description',
                            accessor: 'Description',
                        },
                    ],
                },
            ],
            []
        )
    useEffect(() => {
        fetchItems();
    }, []);

    const [items, setItems] = useState([]);

    const fetchItems = async () => {
        const data = await fetch('/products');
        const items = await data.json();
        setItems(items);
    };

    return (
        <section>
            <div className="container-fluid" >
                <h1 className="mt-5">Product ID, Name, Quantity on hand, Max Quantity, Description {items.length}</h1>
                <div className = 'row padding'>
                <div className="alert alert-info rounded-pill" role="alert">
                    <Table columns={columns} data={items} />
                    </div>
                    </div>
              {/*  <ol key={items.PID}>
                {
                        items.map(item => (
                         <div className="alert alert-info rounded-pill" role="alert">
                            <div className="table">
                                    <li>  {item.Name}, {item.Quantity}, {item.MaxQuantity}, {item.Description} </li>
                                </div>
                            </div>
                    ))
                    }
                </ol>*/}
                </div>
        </section>

    );
}
export default Products;
