import React, { useEffect, useState } from 'react';

function Products() {
    useEffect(() => {
        fetchItems();
    }, []);

    const [items, setItems] = useState([]);

    const fetchItems = async () => {
        const data = await fetch('/products');
        const items = await data.json();
        setItems(items);
    };

    return (
        <section>
            <div class="container-fluid">
                <h1 class="mt-5">Show list of Products here</h1>

                <div class="container-fluid">
                    <h1 class="mt-5">Products</h1>
                    <form method="POST" action="/addTweet">
                        <div class="input-group justify-content-center">
                            <div class="input-group-prepend">
                                <input type="text" name="tweetInput" class="form-control" />
                                <input type="submit" value="Send" class="btn btn-primary mb-2" />
                            </div>
                        </div>
                    </form>
                {
                    items.map(item => (
                        <div class="row padding">
                            <div class="alert alert-info rounded-pill" role="alert">
                                <i class="fa fa-user mr-2"></i> <i>{item.Name} ({item.Quantity}): {item.Description}</i>
                            </div>
                        </div>
                    ))
                }
                </div>
                </div>
        </section>

    );
}
export default Products;
